import React, { useState } from "react";
import { Link } from "react-router-dom";
import Subscribers from './Subscribers'

export default function StoreCard({ storeid, storename, storeemail, storecode, storesubs, storegoods, storetrans }) {

  // console.log(name)
  // console.log(email)
  // console.log(id)


  //---------------STATE-------------
  const [showTransactions, setShowTransactions] = useState(false);
  const [goodsToggle, setGoodsToggle] = useState(false);


  //---------------FUNCTIONALITY-------------
  const toggleGoods = () => {
    setGoodsToggle(!goodsToggle);
  };

  // const subscribers = storesubs.map((sub) => {
  //   return (
  //     <Subscribers
  //       clientid={sub.client_id}
  //       storeid={storeid}
  //       name={storename}
  //       email={storeemail}
  //     />
  //   )
  // })

  const storegood = storegoods.map((good) => (
    <ul key={good.id}>
      <p>{good.name}</p>
      <p>${parseFloat(good.price).toFixed(2)}</p>
      <p>{good.image}</p>
    </ul>
  ))

  // const storetran = storetrans.map((tran) => {
  //   <ul key={tran.id}>
  //   <p>{tran.client.name}</p>
  //   </ul>
  // })
  const [subToggle, setSubToggle] = useState(false)
  const handleSubToggle = () => {
    setSubToggle(!subToggle)
  }

  return (
    <div>
      <div>
        <Link to={`/stores/${storeid}`} style={{ textDecoration: 'none', color: 'inherit' }} >
          <div align='center' style={{ cursor: 'pointer' }}>
            <h2>Store:</h2>
            <p>Name:{storename}</p>
            <p><b>email:</b>{storeemail}</p>
            <p><b>code:</b>{storecode}</p>
            <h2><b>goods:</b></h2>
          </div>
        </Link >
      </div>
      <div>
        <button onClick={toggleGoods}>{!goodsToggle ? 'Show Services' : 'Hide Services'}</button>
        {goodsToggle ? storegood : null}
        <br />
        <br />
        Profile
        <br />
        <br />


        <button onClick={handleSubToggle} >{!subToggle ? 'Subscribe' : 'Unsubscribe'}</button>
        {/* <SubscribeButton /> place subscribe code logic in a componenet that creates a button and handles logic here*/}
        <br />
      </div>
    </div>



  )
}
