import React from "react";

export default function Transactions({trans, client}) {

      // Filter transactions based on the client_id
  const clientTransactions = trans.filter((transaction) => transaction.client_id === clientId);

  return (
    <div>
      <h3>Transactions:</h3>
      {clientTransactions.length > 0 ? (
        clientTransactions.map((transaction) => (
          <div key={transaction.id}>
            <p>
              <strong>Business:</strong> {transaction.store ? transaction.store.name : "N/A"}
            </p>
            <p>
              <strong>Email:</strong> {transaction.store ? transaction.store.email : "N/A"}
            </p>
            <p>
              <strong>Service Received:</strong>{" "}
              {transaction.store && transaction.store.goods_services && transaction.store.goods_services.length > 0
                ? transaction.store.goods_services[0].name
                : "N/A"}
            </p>
            <p>
              <strong>Total Paid:</strong> ${parseFloat(transaction.total_amount).toFixed(2)}
            </p>
          </div>
        ))
      ) : (
        <p>No transactions available for this client.</p>
      )}
    </div>
  );
}
