import React, { useEffect } from 'react';
import { useTheme } from './ThemeContext';

export default function About() {

    const { background, changeBackground } = useTheme();

    useEffect(() => {
      // Change the background based on your logic
      changeBackground('/public/Images/NewBackground.gif');
    }, []);

      // Update the background style of the body
      document.body.style.backgroundImage = `url(${background})`;



    return (
        <div align='center' id="about">
            <img alt='Little happy moose' src="./Images/moose.gif"/>
            <h1>This project was inspired by my love of business and networking.
                <br/>I have implemented my coding knowledge to create a coherent and welcoming
                <br/>environment where members can build a wholesome community based on provided services.</h1>
        </div>
    )
}
