import React, { useState, useEffect } from "react";
import ClientCard from "./ClientCard";

export default function Clients({ clients }) {

    const [shop, setShop] = useState(null)

  useEffect(() => {
    const fetchShopData = async () => {
      try {
        const resp = await fetch("/check_store_session");
        if (resp.ok) {
          const data = await resp.json();
          setShop(data);
        }
      } catch (error) {
        console.error("Error fetching shop data:", error);
      }
    };

    fetchShopData();
  }, []);

  if (!shop) {
    return <div>Loading...</div>;
  }

  const subbedClients = shop?.subscribed_clients || [];
  const mappedSubbedClients = subbedClients.map((client) => client);

  if (mappedSubbedClients.length === 0) {
    return <div>No subscribed clients found.</div>;
  }

  const clientView = mappedSubbedClients.map((client) => (
    <ClientCard
      key={client.id}
      id={client.id}
      name={client.name}
      email={client.email}
    />
  ));

    return (
        <div align='center'>
            {clientView}
        </div>
    )
}
