import { Route, Switch } from 'react-router-dom';
import React, { useState, useEffect } from "react";
import About from "./About";
import Clients from './Clients';
import Home from './Home';
import Navbar from './Navbar';
import Login from './Login';
import StoreAcctPage from './StoreAcctPage';



export default function App() {

    const [clients, setClients] = useState([])
    const [stores, setStores] = useState([])
    const [storeLoggedin, setStoreLoggedIn] = useState(false)
    const [loggedIn, setLoggedIn] = useState(false)

    //this fetch is for clients card
    useEffect(() => {
        fetch('/clients')
            .then((resp) => resp.json())
            .then((data) => setClients(data))
    }, [])

    useEffect(() => {
        fetch('/stores')
            .then((resp) => resp.json())
            .then((data) => setStores(data))
    }, [])

    const store = stores.map((store) => { 
        return (
            name = store
    )})


    return (
        <>
        <header align='center'>
            <h1>COMMUNITY CONNEX!</h1>
        </header>
        <div id='bannerdiv'>
            <Navbar store={store} storeLoggedin={storeLoggedin} />

        </div>
        <div id='maindiv'>
            <Switch>
                <Route exact path="/"><Login loggedIn={loggedIn} storeLoggedin={storeLoggedin} setStoreLoggedIn={setStoreLoggedIn} /></Route>
                <Route exact path='/clients'><Clients clients={clients} /></Route>
                <Route exact path="/About" component={About} />
                <Route exact path='/stores' component={Home}></Route>
                <Route exact path='/stores' component={StoreAcctPage}></Route>
                <Route exact path={`/stores/${store.name}`} component={() => <Home store={store} />} />
                <Route exact path={`/stores/${store.name}/AccountManager`} component={() => <StoreAcctPage store={store} />} />
            </Switch>
        </div>
    </>
    )
}
