import React, { useState } from "react";
import ClientCard from "./ClientCard";

export default function Stores({ stores }) {

    function getSessionStoreId() {
        const sessionId = sessionStorage.getItem("sessionId");
        return sessionId;
      }

    const loggedInStoreId = getSessionStoreId();

    const filteredStores = stores.filter((store) =>
    store.subscribed_clients.some((store) => client.id === loggedInStoreId)
  );



    // console.log(stores.name)
    // console.log(stores.email)
    // console.log(stores.transactions)

    const storeView = filteredStores.map((store) => {
        return (
            <StoreCard key={store.id}
                id={store.id}
                name={store.name}
                email={store.email}
                trans={store.transactions}
            />
        )
    })

    // console.log(stores)


    return (
        <div align='center'>
            {clientView}
        </div>
    )
}
