import React, { useState, useEffect } from 'react';

const TransactionsByStore = () => {

  const [shop, setShop] = useState(null);
  const [clients, setClients] = useState(null)
  const [filteredTransactions, setFilteredTransactions] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch("/check_store_session").then((resp) => {
      if (resp.ok) {
        resp.json().then((store) => setShop(store));
      }
    });
  }, []);

  // Check if shop is null or undefined



  // Check if transactions is null or undefined
  const trans = shop?.transactions || [];
  //-----------------------------------------------------------------------------------------------------------------

  useEffect(() => {
    fetch('/clients')
      .then((resp) => resp.json())
      .then((data) => setClients(data))
      .finally(() => setLoading(false));
  }, [])


  //-----------------------------------------------------------------------------------------------------------------

  useEffect(() => {
    if (!shop || loading) {
      return;
    }

    const trans = shop.transactions || [];
    const allTransactions = clients?.reduce(
      (acc, client) => acc.concat(client.transactions),
      []
    );

    const filtered = allTransactions?.filter((transaction) => {
      return transaction.store.name === shop.name;
    });

    // Convert goods_service_names from string to array
    const transactionsWithArray = filtered?.map((transaction) => ({
      ...transaction,
      goods_service_names: transaction.goods_service_names
      ? transaction.goods_service_names.split(', ')
      : [],
    }));

    setFilteredTransactions(transactionsWithArray);
  }, [shop, clients, loading]);
}
export default TransactionsByStore;

