import React, { useState } from "react";
import Transactions from "./TransactionsCard";

export default function StoreCard({ id, name, email, code, subs, goods, trans }) {

  // console.log(name)
  // console.log(email)
  // console.log(id)


  //---------------STATE-------------
  const [client, setClient] = useState([]);
  const [showTransactions, setShowTransactions] = useState(false);


  //---------------FUNCTIONALITY-------------
  const toggleTransactions = () => {
    setShowTransactions(!showTransactions);
  };

  const subscribers = subs.map((sub) => {
    return <Subscribers
      id={sub.client_id}
    />
  })


  return (
    <div align='center'>
      <h2>Store:</h2>
      <p>Name:{name}</p>
      <p>email:{email}</p>
      <p>code:{code}</p>
      <p>subs:{subscribers}</p>
      <br />
    </div>
  )
}
