import React, { useState, useEffect } from "react";
import StoreCard from "./StoreCard";
import TransactionsByStore from "./TransactionsByStore";


export default function StoreAcctPage({ stores }) {
  const { store_id } = useParams();
  const storeid = stores.find((store) => store.id === parseInt(store_id));
  if (!storeid) {
    return <p>Store data not found.</p>;
  }

  // const store = stores.map((store) => {
  //   return <StoreCard key={store.id}
  //      name={store.name}
  //      email={store.email}
  //      goodsNservices={store.goods_services}
  //      transactions={store.transactions}

  //   />
  // })
    const [store, setStore] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
      const fetchStoreData = async () => {
        try {
          // Retrieve the logged-in store ID from session storage
          const loggedInStoreId = sessionStorage.getItem('store_id');

          if (!loggedInStoreId) {
            // Handle the case where store ID is not available in session storage
            return;
          }

          // Fetch the store data based on the logged-in store ID
          const response = await fetch(`/stores/${loggedInStoreId}`);
          const data = await response.json();

          setStore(data);
        } catch (error) {
          console.error('Error fetching store data:', error);
        } finally {
          setLoading(false);
        }
      };

      fetchStoreData();
    }, []);

    if (loading) {
      return <p>Loading...</p>;
    }

    if (!store) {
      // Handle the case where store data is not available
      return <p>Store data not found.</p>;
    }


    return (
      <div>
        <h2>Welcome to the Store Dashboard, {store.name}!</h2>
        <p>Store Name: {store.name}</p>
        <TransactionsByStore storeId={store.id} />
        {/* Add more components or details as needed */}
      </div>
    );
  };
