// LoginForm.js
import { useHistory } from "react-router-dom";
import React, { useState } from 'react';
import { Formik, Field, Form, ErrorMessage } from 'formik';
import * as Yup from 'yup';

const ClientLoginForm = ({ getClients, setClientLoggedin, clientLoggedIn }) => {

  const history = useHistory()
  const [message, setMessage] = useState('');



  const initialValues = {
    email: '',
    password: '',
  };

  const validationSchema = Yup.object().shape({
    email: Yup.string().email('Invalid email address').required('Required'),
    password: Yup.string().required('Required'),
  });

  const handleSubmit = async (values, { setSubmitting }) => {
    try {
      setMessage('Logging in...');
      // Send login request to your backend
      const response = await fetch('/client_login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(values),
      });

      if (response.ok) {
        await response.json();

        // getClients(client); // this will allow me to alter state
        setMessage('Login successful. Redirecting to home...');
        setTimeout(() => {
          history.push('/About');// After 4 seconds, navigate to the home page
        }, 2000);
      } else {
        const error = await response.json();
        console.error('Login failed:', error);
      }
    } catch (error) {
      console.error('Error during login:', error);
    }

    setSubmitting(false);
  };

    return (
      <div>
        {message ? (
          <div id="login-message">
            <div>{message}</div>
          </div>
        ) : (
          <div>
            <div>Client Login:</div>

            <Formik
              initialValues={initialValues}
              validationSchema={validationSchema}
              onSubmit={handleSubmit}
            >
              <Form>
                <br />
                <div>
                  <label htmlFor="email">Email:</label>
                  <Field type="email" id="email" name="email" />
                  <ErrorMessage name="email" component="div" />
                </div>
                <br />
                <div>
                  <label htmlFor="password">Password:</label>
                  <Field type="password" id="password" name="password" />
                  <ErrorMessage name="password" component="div" />
                </div>
                <br />
                <div>
                  <button type="submit">Login</button>
                </div>
              </Form>
            </Formik>
          </div>
        )}
      </div>
    );
  };


export default ClientLoginForm;















