import React, {useState} from "react";
import ClientCard from "./ClientCard";
import Transactions from "./Transactions";

export default function Clients({ clients }) {

    // console.log(clients.name)
    // console.log(clients.email)
    // console.log(clients.transactions)
    const [showTransactions, setShowTransactions] = useState(false);



    const toggleTransactions = () => {
      setShowTransactions(!showTransactions);
    };

    const trans = clients.map((client) => {
        client.transactions.map((tran) => {
            console.log(tran)
            return (
                <Transactions key={tran.id}
                    name={tran.store ? tran.store.name : 'N/A'}
                    email={tran.store ? tran.store.email : 'N/A'}
                    service={tran.store && tran.store.goods_services.length() > 0 ? tran.store.goods_services[0].name : 'N/A'}
                    price={tran.store && tran.store.goods_services.length() > 0 ? tran.store.goods_services[0].price : 'N/A'}
                />
            )
        })})

    const clientView = clients.map((client) => {
    return (
        <ClientCard key={client.id}
            id={client.id}
            name={client.name}
            email={client.email}
        />
    )}) 

    // console.log(clients)


    return (
        <div align='center'>
            {clientView}
        </div>
    )
}
